var React = require('react');
var Base = require('./base');

var Datalist = React.createClass({displayName: "Datalist",
  propTypes: {
    collection: React.PropTypes.array.isRequired
  },

  render:function()                {
    var options = this.props.collection.map(function(v)  {return React.createElement("option", {key: v.toString().toLowerCase(), value: v});});
    var listId = [this.props.getId(), 'list'].join('_');

    return (
      React.createElement("span", null, 
        React.createElement(Base, React.__spread({},  this.props, {list: listId})), 
        React.createElement("datalist", {id: listId}, 
          options
        )
      )
    );
  }
});

module.exports = Datalist;
